package pl.bsb.b2btester.util;

import org.junit.Test;
import static org.junit.Assert.*;
import pl.bsb.b2btester.model.manager.CryptoManager;
import pl.bsb.b2btester.signature.VerifierException;

/**
 *
 * @author swarczak
 */
public class rootTest {
    
    private String root = "";
    private String decrypt = "";
    
    public rootTest() {
    }
    
    @Test
    public void verifyRoot() {
        assertTrue(CryptoManager.checkRootValidity(Base64.decode(root)));
    }
    
    @Test
    public void verifyNotRoot() throws VerifierException {
        assertFalse(CryptoManager.checkRootValidity(Base64.decode(decrypt)));
    }
}
